mapper.update:
update sku_stock
set lock_num = lock_num + #{skuNum},
    available_num = available_num - #{skuNum}
where sku_id = #{skuId}

update sku_stock
set lock_num = lock_num - #{skuNum},
    available_num = available_num + #{skuNum}
where sku_id = #{skuId}

update sku_stock
set lock_num = lock_num - #{skuNum},
    total_num = total_num - #{skuNum},
    sale_num = sale_num + #{skuNum}
where sku_id = #{skuId}
